// Fetch orchestrator status and update display
const status = context.panel.data && context.panel.data.series && context.panel.data.series[0];
let isRunning = false;
let formValues = {};

if (status) {
  const runningField = status.fields.find(f => f.name === 'running');
  isRunning = runningField && runningField.values[0];

  // Control submit button based on running state
  if (isRunning) {
    context.panel.disableSubmit();
  } else {
    context.panel.enableSubmit();
  }

  // Time remaining
  const remainingField = status.fields.find(f => f.name === 'remaining_seconds');
  const remaining = remainingField ? remainingField.values[0] : null;
  if (remaining !== null && remaining !== undefined && remaining > 0) {
    const mins = Math.floor(remaining / 60);
    const secs = remaining % 60;
    formValues.remaining_display = `${mins}m ${secs}s`;
  } else if (isRunning) {
    formValues.remaining_display = 'Expired';
  } else {
    formValues.remaining_display = 'N/A';
  }

  // Job status
  const jobStateField = status.fields.find(f => f.name === 'job_state');
  const jobState = jobStateField ? jobStateField.values[0] : null;
  const jobIdField = status.fields.find(f => f.name === 'job_id');
  const jobId = jobIdField ? jobIdField.values[0] : null;
  if (jobId) {
    formValues.status_display = `${jobState || 'PENDING'} (Job: ${jobId})`;
  } else {
    formValues.status_display = 'Idle';
  }
} else {
  context.panel.enableSubmit();
  formValues.status_display = 'Idle';
  formValues.remaining_display = 'N/A';
}

context.panel.setFormValue(formValues);

