const recipeEl = context.panel.elements.find(el => el.id == "recipe_name");
const recipe = recipeEl ? recipeEl.value : "";

// Get session status directly from data (more reliable than element value)
let isSessionRunning = false;
if (context.panel.data && context.panel.data.series) {
  const statusSeries = context.panel.data.series.find(s => s.fields && s.fields.some(f => f.name === 'running'));
  if (statusSeries) {
    const runningField = statusSeries.fields.find(f => f.name === 'running');
    isSessionRunning = runningField && runningField.values && runningField.values[0];
  }
}

// 1. Handle Recipe Change (Load Defaults)
if (context.panel.data.prevSelected !== recipe && context.panel.data.series && context.panel.data.series[0]) {
  const data = context.panel.data.series[0].fields;
  if (data) {
    const idx = data.find(s => s.name == "name").values.findIndex(el => el == recipe)

    // Safely parse resources
    let parameters = {};
    const resourceField = data.find(s => s.name == "resources");
    if (resourceField) {
      const rawVal = resourceField.values[idx];
      if (typeof rawVal === 'string') {
        try {
          parameters = JSON.parse(rawVal);
        } catch (e) {
          console.error("Failed to parse resources JSON", e);
        }
      } else {
        parameters = rawVal || {};
      }
    }

    // Safely parse recipe parameters
    let recipeParams = {};
    const paramsField = data.find(s => s.name == "parameters");
    if (paramsField) {
      const rawVal = paramsField.values[idx];
      if (typeof rawVal === 'string') {
        try {
          recipeParams = JSON.parse(rawVal);
        } catch (e) {
          console.error("Failed to parse parameters JSON", e);
        }
      } else {
        recipeParams = rawVal || {};
      }
    }

    let defaultModel = "";
    if (recipeParams && recipeParams["model"]) {
      defaultModel = recipeParams["model"].default || "";
    }

    context.panel.setFormValue({
      "_session_running": isSessionRunning,
      "recipe_name": recipe,
      "config.nodes": parameters["nodes"] || 1,
      "config.cpus": parameters["cpu"] || 1,
      "config.gpus": parameters["gpu"] || 0,
      "config.memory": (parameters["memory"] || "4G").replace("i", "").replace(" ", ""),
      "config.time_limit": parameters["time_limit"] || 15,
      "config.model": defaultModel,
      "config.replicas": 1,
      "show_advanced": false
    })
  }
}
context.panel.data.prevSelected = recipe;

// 2. Handle Replicas Logic
if (recipe.includes("replicas")) {
    const replicasEl = context.panel.elements.find(el => el.id == "config.replicas");
    const replicas = replicasEl ? replicasEl.value : 1;

    // Only update if replicas changed
    if (context.panel.data.prevReplicas !== replicas) {
        const gpusPerNode = 4;
        let nodes = Math.ceil(replicas / gpusPerNode);
        let gpus = 4;

        if (nodes === 1) {
            gpus = replicas;
        }

        context.panel.setFormValue({
            "_session_running": isSessionRunning,
            "recipe_name": recipe,
            "config.nodes": nodes,
            "config.gpus": gpus
        });
    }
    context.panel.data.prevReplicas = replicas;
}

// 3. Handle Single Node Logic
if (recipe.includes("single-node")) {
    const nodesEl = context.panel.elements.find(el => el.id == "config.nodes");
    if (nodesEl && nodesEl.value !== 1) {
         context.panel.setFormValue({"_session_running": isSessionRunning, "config.nodes": 1});
         context.grafana.notifyWarning(['Configuration', 'Single node recipes are restricted to 1 node.']);
    }
}

