options.elementValueChanged:
const selected = context.panel.elements.find(el => el.id == "recipe_name").value
if (context.panel.data.prevSelected !== selected && context.panel.data.series && context.panel.data.series[0]) {
  const data = context.panel.data.series[0].fields;
  if (data) {
    const idx = data.find(s => s.name == "name").values.findIndex(el => el == selected)

    // Safely parse resources
    let parameters = {};
    const resourceField = data.find(s => s.name == "resources");
    if (resourceField) {
      const rawVal = resourceField.values[idx];
      if (typeof rawVal === 'string') {
        try {
          parameters = JSON.parse(rawVal);
        } catch (e) {
          console.error("Failed to parse resources JSON", e);
        }
      } else {
        parameters = rawVal || {};
      }
    }

    // Safely parse recipe parameters to find default model
    let recipeParams = {};
    const paramsField = data.find(s => s.name == "parameters");
    if (paramsField) {
      const rawVal = paramsField.values[idx];
      if (typeof rawVal === 'string') {
        try {
          recipeParams = JSON.parse(rawVal);
        } catch (e) {
          console.error("Failed to parse parameters JSON", e);
        }
      } else {
        recipeParams = rawVal || {};
      }
    }

    let defaultModel = "";
    if (recipeParams && recipeParams["model"]) {
      defaultModel = recipeParams["model"].default || "";
    }

    context.panel.setFormValue({
      "recipe_name": selected,
      "config.nodes": parameters["nodes"] || 1,
      "config.cpus": parameters["cpu"] || 1,
      "config.gpus": parameters["gpu"] || 0,
      "config.memory": (parameters["memory"] || "4G").replace("i", "").replace(" ", ""),
      "config.time_limit": parameters["time_limit"] || 15,
      "config.model": defaultModel
    })
  }
}
context.panel.data.prevSelected = selected

elements.showIf:
const elements = context.panel.elements;
if (!elements) return false;
const recipe = elements.find(el => el.id === 'recipe_name');
return recipe && (recipe.value || '').toLowerCase().includes('vllm');

resetAction.code:
if (context.panel.response) {
  context.grafana.notifySuccess(['Update', 'Values updated successfully.']);
  context.grafana.refresh();
} else {
  context.grafana.notifyError(['Update', 'An error occurred updating values.']);
}

update.code:
if (context.panel.response && context.panel.response.ok) {
  context.grafana.notifySuccess(['Service started', 'Service has been successfully started.']);
  context.grafana.refresh();
} else {
  context.grafana.notifyError(['Unable to start service', 'An error occurred starting the service.']);
}

update.getPayload:
const payload = { config: { resources: {} } };
context.panel.elements.forEach((element) => {
  if (element.value === undefined || element.value === null || element.value === '') return;
  if (element.id === 'recipe_name') {
    payload.recipe_name = element.value;
  } else if (element.id === 'config.nodes') {
    payload.config.nodes = parseInt(element.value) || 1;
  } else if (element.id === 'config.cpus') {
    payload.config.resources.cpu = parseInt(element.value) || 1;
  } else if (element.id === 'config.gpus') {
    payload.config.resources.gpu = parseInt(element.value) || 0;
  } else if (element.id === 'config.memory') {
    payload.config.resources.memory = element.value;
  } else if (element.id === 'config.model') {
    payload.config.model = element.value;
  } else if (element.id === 'config.time_limit') {
    payload.config.resources.time_limit = parseInt(element.value) || 15;
  }
});
console.log('Sending payload:', JSON.stringify(payload));
return payload;
